script:
frappe.ui.form.on('Sales Order', {
    onload: function(frm) {
        // Check if the sales order is being created from a quotation
        if (frm.doc.quotation) {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Quotation',
                    name: frm.doc.quotation
                },
                callback: function(r) {
                    if (r.message) {
                        // Pre-populate the customer field
                        frm.set_value('customer', r.message.customer);
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Quotation', {
    onload: function(frm) {
        // Check if the quotation is being created from a project
        console.log('Hello from qutation client 2');
        if (frm.doc.project) {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Project',
                    name: frm.doc.project
                },
                callback: function(r) {
                    if (r.message) {
                        // Pre-populate the customer field
                        //frm.set_value('customer', r.message.custom_project);
                        frm.set_value('party_name', r.message.customer);
                        console.log(r.message);
                    }
                }
            });
        }
    }
});
